import React, { Component } from 'react';

export default class GroceryCard extends Component {
    state = {
        clicker: 1
    }

    decreaseHandler = () => {
        this.setState({ clicker: this.state.clicker - 1})

        const { clicker } = this.state

        fetch(`http://localhost:3000/ingredients/${this.props.grocery.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 
                quantity: clicker,
            })
        })
    }

    increaseHandler = () => {
        this.setState({ clicker: this.state.clicker + 1})

        fetch(`http://localhost:3000/ingredients/${this.props.grocery.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ quantity: this.state.clicker})
        })
    }

    render(){
        const { title, picture, id, quantity} = this.props.grocery
        return(
            <div className="ingredient-card" data-id={id}>
                <img src={picture} alt=""></img>
                <div>
                <h3>{title}</h3>
                <button className="button" onClick={this.decreaseHandler}>-</button><button className="button" onClick={this.increaseHandler}>+</button>
                <br></br>
                <p>Quantity: {quantity}</p>
                </div>
            </div>
        )
    }
}
