import React, { Component } from 'react';
import Nav from './Nav'

export default class Recipes extends Component {
    constructor(){
        super()
        this.state = {
            searchTerm: "",
            recipes: []
        }
    }

    changeHandler = (e) => {
        this.setState({ searchTerm: e.target.value})

