import React, { Component } from 'react';
import Nav from './Nav';
import GroceryCard from './GroceryCard';

export default class GroceryList extends Component {
    constructor(){
        super()
        this.state = {
            barcode: "",
            ingredients: []
        }
    }

    submitHandler = (e) => {
        e.preventDefault()

        fetch('http://localhost:3000/scans', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ barcode: this.state.barcode})
        }).then( res => res.json())
        .then( json => this.setState({
            ingredients: [...this.state.ingredients, json], 
            barcode: ''
        }))
    }

    changeHandler = (e) => {
        this.setState({ barcode: e.target.value})
    }


    componentDidMount(){
        let token = localStorage.getItem('token')
        fetch('http://localhost:3000/ingredients',{
            headers: {
                'Content-Type': 'application/json',
                Authorization: token
            }
        })
        .then( res => res.json())
        .then( json => this.setState({ ingredients: json}, () => this.populate()))
    }

    populate = () => {


        if (localStorage.getItem('token')){
            // console.log('hello')
             let groceries = this.state.ingredients.map( item => {
               return <GroceryCard key={item.id} grocery={item} />
            })
        }else{
            let groceries = <h1 className="no-pantry">Login or Register to see your Pantry!</h1>
        }
        return groceries

    }

    render(){


        return(
            <div className='header'>
            <Nav />
                <h1>Pantry</h1>
                    <form onSubmit={this.submitHandler}>
                    <input type="text" autoFocus onChange={this.changeHandler} className="hidden-input" value={this.state.barcode}></input>
                    </form>
                    <div className="grocery-cont">
                        {this.populate}
                    </div>
            </div>
        )
    }
}
