import React, { Component } from 'react';
import Nav from './Nav';

export default class Register extends Component {
    state = {
        name: "",
        username: "",
        password: ""
    }

    submitHandler = (event) => {
        event.preventDefault()

        fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: this.state.name,
                username: this.state.username,
                password_digest: this.state.password
            })
        }).then( res => res.json())
        .then(console.log, this.setState({ name: "", username: "", password: ""}))
    }

    changeHandler = (event) => {
        this.setState({ [event.target.name]: event.target.value})
    }

    render(){
        return(
            <div>
                <header className="header">
                <Nav />
                <div className="form-container">
                    <form className='form' onSubmit={this.submitHandler}>
                    <h1>Register</h1>
                        <input type='text' placeholder='Name' name="name" className="form-input" value={this.state.name} onChange={this.changeHandler}></input>
                        <br></br>
                        <input type='text' placeholder='Username' name="username" className="form-input" value={this.state.username} onChange={this.changeHandler}></input>
                        <br></br>
                        <input type='text' placeholder='Password' name="password" className="form-input" value={this.state.password} onChange={this.changeHandler}></input>
                        <br></br>
                        <button className='form-submit' onClick={this.submitHandler}>Login</button>
                    </form>
                </div>
                </header>
            </div>
        )
    }
}
