import React, { Component } from 'react';
import GroceryCard from './GroceryCard';

export default class GroceryList extends Component {
    constructor(){
        super()
        this.state = {
            barcode: "",
            ingredients: [],
            user: {},
            uIng: [],
            state: []
        }
    }

    submitHandler = (e) => {
        e.preventDefault()

        fetch('http://localhost:3000/scans', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ 
                barcode: this.state.barcode,
                user_id: this.props.user.id
            })
        }).then( res => res.json())
        .then( json => this.setState({
            ingredients: [...this.state.ingredients, json], 
            barcode: ''
        }))
    }

    changeHandler = (e) => {
        this.setState({ barcode: e.target.value})
    }


    componentDidMount = () => {

        let token = localStorage.getItem('token')

        if(token){
          fetch('http://localhost:3000/current_user', {
            headers: {
              'Content-Type': 'application/json',
              'Accepts': 'application/json',
              Authorization: token
            }
          }).then( res => res.json())
          .then(json => this.setState({ user: json}, () => this.getUserIng(this.state)))
        }
    }

    getUserIng = (state) => {
          let token = localStorage.getItem('token')

          fetch('http://localhost:3000/user_ingredients',{
            headers: {
                'Content-Type': 'application/json',
                Authorization: token
            }
        })
        .then( res => res.json())
        .then( json => {
            json.forEach( (ui) => {
                let id = state.user.id

                if(parseInt(ui.user_id) === id){
                    this.setState({ uIng: [...state.uIng, ui]}, () => this.anotherFunction(this.state.uIng))
                }
            })
        })
    }

    anotherFunction = (state) => {
        let token = localStorage.getItem('token')
        state.forEach( ui => {

            fetch('http://localhost:3000/ingredients',{
            headers: {
                'Content-Type': 'application/json',
                Authorization: token
            }
        }).then( res => res.json())
        .then( json => {
            json.forEach( ing => {

                if(parseInt(ui.ingredient_id) === ing.id){
                    this.setState({ ingredients: [...this.state.ingredients, ing]})
                }
            })
        })
        })
    }

    rerender = (object) => {
        console.log(this.state.state)
        this.setState({ state: object }, () => console.log(this.state.state))
        console.log('done')
    }

    render(){

        let groceries;

        if (localStorage.getItem('token')){

             groceries = this.state.ingredients.map( item => {
               return <GroceryCard key={item.id} grocery={item} user={this.props.user} change={this.rerender}/>
            })
        }else{
            groceries = <h1 className="no-pantry">Login or Register to see your Pantry!</h1>
        }

        // console.log(this.props.user)

        return(
            <div>
                <h1>Pantry</h1>
                    <form onSubmit={this.submitHandler}>
                    <input type="text" autoFocus onChange={this.changeHandler} className="hidden-input" value={this.state.barcode}></input>
                    </form>
                    <div className="grocery-cont">
                        {groceries}
                    </div>
            </div>
        )
    }
}
