import React, { Component } from 'react';

export default class GroceryCard extends Component {
    state = {
        clicker: "",
        class: "ingredient-card",
        clicked: false,
    }

    componentDidMount(){
        fetch(`http://localhost:3000/ingredients/${this.props.grocery.id}`)
        .then( res => res.json())
        .then( json => this.setState({ clicker: json.quantity}))
    }


    decreaseHandler = () => {
        const { clicker } = this.state

        fetch(`http://localhost:3000/ingredients/${this.props.grocery.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ quantity: clicker - 1})
        }).then( res => res.json())
        .then( json => this.setState({ clicker: json.quantity}))
    }


    increaseHandler = () => {
        fetch(`http://localhost:3000/ingredients/${this.props.grocery.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ quantity: this.state.clicker + 1})
        }).then( res => res.json())
        .then( json => this.setState({ clicker: json.quantity}))
    }


    changeColor = () => {
        if(this.state.class === "ingredient-card"){
            this.setState({ class: "ingredient-card green"})
        }else{
            this.setState({ class: "ingredient-card"})
        }
    }


    deleteCard = () => {
        let token = localStorage.getItem('token')

        fetch('http://localhost:3000/user_ingredients',{
            headers: {
                'Content-Type': 'application/json',
                Authorization: token
            }
        }).then( res => res.json())
        .then( json => {
            json.forEach( ing => {

                if(this.props.user.id === parseInt(ing.user_id) && this.props.grocery.id === parseInt(ing.ingredient_id)){
                    fetch(`http://localhost:3000/user_ingredients/${ing.id}`,{
                        method: 'DELETE',
                        headers: {
                            'Content-Type': 'application/json',
                            Authorization: token
                        }
                    }).then( res => res.json())
                    .then( json => {
                        console.log('hi')
                        const { userIng } = this.props

                        for(let i = 0; i < userIng.length; i++){

                            console.log(parseInt(userIng[i].ingredient_id), parseInt(ing.id))

                            if(parseInt(userIng[i].ingredient_id) === parseInt(ing.id)){
                                console.log('weve reached')
                            }
                        }

                    })


                }
            })
        })
    }

    render(){

        const { title, picture, id} = this.props.grocery

        const quantity = this.state.clicker

        return(
            <div className={this.state.class} data-id={id}>
                <button className="delete-button" onClick={this.deleteCard}>X</button>
                <img src={picture} alt="" onClick={this.changeColor}></img>
                    <div>
                        <h3>{title}</h3>
                        <button className="button" onClick={this.decreaseHandler}>-</button><button className="button" onClick={this.increaseHandler}>+</button>
                        <br></br>
                        <p>Quantity: {quantity}</p>
                    </div>
            </div>
        )
    }
}
