import React, { Component } from 'react';
import './App.css';
import LandingPage from './components/LandingPage'
import { Route, Switch } from 'react-router-dom';
// import Nav from './components/Nav'
import Login from './components/Login';
import Register from './components/Register';
import GroceryList from './components/GroceryList';
import Recipes from './components/Recipes';
import { withRouter } from 'react-router-dom'

class App extends Component {
  state = {
    user: {},
    class: "downArrow bounce"
  }

  componentDidMount(){
    let token = localStorage.getItem('token')
    console.log(token)
    if(token){
      fetch('http://localhost:3000/current_user', {
        headers: {
          'Content-Type': 'application/json',
          'Accepts': 'application/json',
          Authorization: token
        }
      }).then( res => res.json())
      .then(console.log)
    }else{
      this.props.history.push('/register')
    }
  }

  submitHandler = (event, state) => {
    event.preventDefault()
    console.log(state.user)

    fetch('http://localhost:3000/users', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json', 
            Accepts: 'application/json', 

        },
        body: JSON.stringify({
            name: state.name,
            username: state.username,
            password: state.password
        })
    }).then( res => res.json())
    .then( json => {
      if(json.jwt){
        localStorage.setItem('token', json.jwt)
        this.setState({ user: json})
      }
    })

  }

  loginHandler = (e, state) => {
    console.log(state.password, state.username)
    e.preventDefault()

    fetch('http://localhost:3000/login', {
        method: 'POST', 
        headers: { 
          'Content-type': 'application/json',
          'Accepts': "application/json"
      }, 
      body: JSON.stringify({
        username: state.username,
        password: state.password
      })
    }).then( res => res.json())
    .then( json => {
      if(json.jwt){
        localStorage.setItem('token', json.jwt)
        this.setState({ user: json})
      }
    })

    this.props.history.push('/grocerylist')
}


  scrollHandler = (event) => {
    this.setState({ class: ""})
  }


  render() {
    return (
      <div onScroll={this.scrollHandler} style={{height: '100vh', overflow: 'scroll'}}>
        <Switch>
          <Route path='/login' render={() => (<Login loginHandler={this.loginHandler}/>)}/>
          <Route path='/register' render={() => (<Register submitHandler={this.submitHandler}/>)}/>
          <Route path='/home' render={() => (<LandingPage class={this.state.class} scrollHandler={this.scrollHandler}/>)}/>
          <Route path='/grocerylist' component={GroceryList}/>
          <Route path='/recipes' component={Recipes}/>
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
