import React, { Component } from 'react';
import './App.css';
import LandingPage from './components/LandingPage'
import { Route, Switch } from 'react-router-dom';
// import Nav from './components/Nav'
import Login from './components/Login';
import Register from './components/Register';
import GroceryList from './components/GroceryList';
import Recipes from './components/Recipes';
import { withRouter } from 'react-router-dom'

class App extends Component {
  state = {
    user: {},
  }

  componentDidMount(){
    let token = localStorage.getItem('token')
    if(token){

    }else{
      this.props.history.push('/home')
    }
  }
  submitHandler = (event, state) => {
    event.preventDefault()
    console.log(state)

    fetch('http://localhost:3000/users', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json', 
            Accepts: 'application/json', 

        },
        body: JSON.stringify({
            name: state.name,
            username: state.username,
            password: state.password
        })
    }).then( res => res.json())
    .then( json => this.setState({ user: json}, () => console.log(this.state)))

  }


  scrollHandler = () => {
    console.log('doing it')
  }
  render() {
    return (
      <div onScroll={this.scrollHandler} style={{height: '100vh', overflow: 'scroll'}}>
        <Switch>
          <Route path='/login' component={Login}/>
          <Route path='/register' render={() => (<Register submitHandler={this.submitHandler}/>)}/>
          <Route path='/home' component={LandingPage}/>
          <Route path='/grocerylist' component={GroceryList}/>
          <Route path='/recipes' component={Recipes}/>
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
