import React, { Component } from 'react';
import RecipeCard from './RecipeCard';

export default class Recipes extends Component {
    constructor(){
        super()
        this.state = {
            searchTerm: "",
            recipes: []
        }
    }

    changeHandler = (e) => {
        this.setState({ searchTerm: e.target.value})

        console.log(this.state)

    }


    submitHandler = (e) => {
        e.preventDefault()
        const searchTerm = this.state.searchTerm
        console.log(searchTerm)

        fetch(`https://api.edamam.com/search?q=${searchTerm}&app_id=f2e29b18&app_key=618653b4bcb9eae9d0711ac20220e002`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then( res => res.json())
        .then(json => this.setState({ recipes: json.hits}, () => console.log(json.hits)))

        // debugger
    }

    render(){

        const recipeCard = this.state.recipes.map( rec => {
            return <RecipeCard recipe={rec.recipe}/>
            // console.log(rec.recipe)
        })

        return(
            <div>
                <h1>Recipes</h1>
                <form onSubmit={this.submitHandler}>
                    <input type='text' onChange={this.changeHandler}></input>
                    <button onClick={this.submitHandler}>Search</button>
                </form>

                <div>
                    {recipeCard}
                </div>
            </div>
        )
    }
}
